window.PronosoftstickerLib = {
  // ğŸ”„ Nouvelle base URL vers le dossier dist/
  baseURL: 'https://cdn.jsdelivr.net/gh/Jonnhy2255/Pronosoftsticker-lib/dist/',

  insertSticker: function(name, containerId, options = {}) {
    const container = document.getElementById(containerId);
    if (!container) {
      console.error(`âŒ Container avec l'id "${containerId}" introuvable.`);
      return;
    }

    // ğŸ§¹ Nettoie le conteneur
    container.innerHTML = '';

    const img = document.createElement('img');
    img.src = this.baseURL + name + '.svg';
    img.alt = name;

    // ğŸ“ Styles configurables
    img.style.width = options.width || '100px';
    img.style.height = options.height || 'auto';
    img.style.margin = options.margin || '5px';

    // âœ… Insertion
    container.appendChild(img);

    // ğŸªµ Log visuel
    const log = document.createElement('div');
    log.textContent = `âœ”ï¸ Sticker "${name}" insÃ©rÃ© depuis ${img.src}`;
    log.style.fontSize = '0.9em';
    log.style.color = 'green';
    log.style.marginTop = '5px';
    container.appendChild(log);
  },

  list: function() {
  return ['smile', 'cat', 'heart', 'like', 'wow'];
}
};
